mapper.select:
select pro_idx, pro_name, pro_writer, pro_price, pro_amount, pro_content, pro_image, pro_company, pro_video, pro_category1, pro_category2, pro_category3, pro_reg_date, pro_modify_date, floor(pro_price - pro_price*(pro_sale/100)) AS pro_sale_price
from bec_product
where pro_idx = #{pro_idx}

SELECT pro_idx, pro_name, pro_writer, pro_price, pro_sale, pro_amount, pro_content, pro_image, pro_company, pro_video, pro_category1, pro_category2, pro_category3, pro_reg_date, pro_modify_date, floor(pro_price - pro_price*(pro_sale/100)) AS pro_sale_price
FROM bec_product
WHERE 1=1

    AND pro_category1 = #{pro_category1}


    AND pro_category2 = #{pro_category2}


    AND pro_category3 = #{pro_category3}


    AND (pro_name LIKE CONCAT('%', #{search_word}, '%') OR pro_content LIKE CONCAT('%', #{search_word}, '%'))



        ORDER BY ${sortField} ${sortDir}


        ORDER BY pro_reg_date DESC


LIMIT #{page_skip_count}, #{page_size}

SELECT COUNT(*)
FROM bec_product
WHERE 1=1

    AND pro_category1 = #{pro_category1}


    AND pro_category2 = #{pro_category2}


    AND pro_category3 = #{pro_category3}


    AND (pro_name LIKE CONCAT('%', #{search_word}, '%') OR pro_content LIKE CONCAT('%', #{search_word}, '%'))
