mapper.select:
select qna_idx, member_id, qna_title, qna_content, qna_secret, qna_answer_YN, qna_category, qna_answer, qna_reg_date, qna_answer_date
from bec_qna
where qna_idx=#{qna_idx}

mapper.update:
update bec_qna
set qna_title=#{qna_title}, qna_content=#{qna_content}
where qna_idx=#{qna_idx}

update bec_qna
set qna_answer=#{qna_answer}, qna_answer_date=now(), qna_answer_YN='Y'
where qna_idx=#{qna_idx}

update bec_qna
set qna_answer_YN='N', qna_answer=''
where qna_idx=#{qna_idx}

mapper.select:
select qna_idx, member_id, qna_title, qna_content, qna_secret, qna_answer_YN, qna_category, qna_answer, qna_reg_date, qna_answer_date
from bec_qna
where qna_category=#{arg0}
order by qna_idx desc
    limit #{arg1.page_skip_count}, #{arg1.page_size}
